const st = {
  name: '',
  alias_name: '',
  splash_uri: '',
  splash_intro: ''
};
export default function profileReducer(actions = {type:'default'}, state = st) {
  switch(actions.type) {
    case 'default':
      return state;
    case 'SET_PROFILE':
      const nwState = { ...state, ...actions.payload };
      return  nwState;
    default:
      return state;
  }
}
