import api from '@@/services/api';

function setProfile({ 
  name = '',
  alias_name = '',
  splash_uri = '',
  splash_intro = '',
} = {}) {
  return {
    type: 'SET_PROFILE',
    payload: {
      name,
      alias_name,
      splash_uri,
      splash_intro,
    }
  }
}

function getProfile(userID) {
  return async (dispatch) => {
    const result = await api.get_profile(userID);
    console.log(result);
    dispatch(setProfile(result))
  }
}
export {
  getProfile,
};
